package ru.pogodaev.leetcode.n392;

import java.util.Arrays;

/**
 * Given two strings s and t, check if s is a subsequence of t.
 *
 * A subsequence of a string is a new string that is formed from the original string by deleting some (can be none)
 * of the characters without disturbing the relative positions of the remaining characters.
 * (i.e., "ace" is a subsequence of "abcde" while "aec" is not).
 * */


public class IsSubsequence {

    public static final String first = "leeeeetcode";
    public static final String second = "";

    public static void main(String[] args) {
        System.out.println(isSubsequence("abc", "ahbgdc"));
        System.out.println(isSubsequence("axc", "ahbgdc"));
        System.out.println(isSubsequence("aaaaaa", "bbaaaa"));
        System.out.println(isSubsequence("b", "c"));
        System.out.println(isSubsequence(first, second));

    }


    public static boolean isSubsequence(String s, String t) {
        char[] ss = s.toCharArray();
        char[] tt = t.toCharArray();
        int[] indexes = new int[ss.length];
        int i = 0;
        int j = 0;
        for (char m : ss) {

            boolean found = false;


            for (int k = j; k < tt.length; k++) {
                if (m == tt[k]) {
                    indexes[i] = k;
                    j=k;
                    tt[k] = 0;
                    i++;
                    found = true;
                    break;
                }
            }
            if (!found) return false;
        }


        int[] cln = indexes.clone();

        Arrays.sort(cln);

        return Arrays.equals(indexes, cln);

    }
}
